INSERT INTO public.articles (title, content, category, author, published_at, read_time_minutes) VALUES
('Cybersecurity Framework Implementation', 'Comprehensive cybersecurity strategy implementation for a healthcare organization, including zero-trust architecture, employee training, and incident response procedures. Reduced security incidents by 85% and achieved compliance with HIPAA and SOC 2 requirements.', 'Cybersecurity', 'Amanda Foster', now(), 12),
('Customer Experience Digital Journey', 'End-to-end customer experience transformation involving persona mapping, journey optimization, and omnichannel strategy implementation. Resulted in 45% improvement in customer satisfaction scores and 30% increase in customer lifetime value.', 'Customer Experience', 'Sarah Johnson', now(), 8),
('Data Governance and Analytics Platform', 'Implementation of enterprise-wide data governance framework and modern analytics platform. Enabled self-service analytics for 200+ business users and improved data quality by 70% across all departments.', 'Data Strategy', 'Michael Chen', now(), 11),
('Agile Transformation at Scale', 'Large-scale agile transformation for a 5000+ employee organization, including training, coaching, and cultural change management. Improved delivery speed by 60% and employee satisfaction by 40%.', 'Agile Transformation', 'David Rodriguez', now(), 9);